module.exports={

	out:function(res,code,option){
		if(code=="no_controller"){
			this.err_no_controller(res,option);
		}
		else if(code=="no_exists_controller"){
			this.err_no_exists_controller(res,option);
		}
		else if(code=="no_exists_method"){
			this.no_exists_method(res,option);
		}
		else if(code=="no_exist_view"){
			this.no_exist_view(res,option);
		}
	},
	err_no_controller:function(res,option){
		res.writeHead(200,{"Content-Type":"text/html"});
		var str="<pre>Error:";
		str+="Controller was not found. ("+option.controller_name+"_Controller).\n";
		str+="</pre>";
		res.write(str);
		res.end();
		return;
	},
	err_no_exists_controller:function(res,option){
			res.writeHead(200,{"Content-Type":"text/html"});
			var str="<pre>Error:";
			str+="The "+option.controller_name+"_Controller class was not found in the loaded "+option.controller_name+"_Controller file.";
			str+="</pre>";
			res.write(str);
			res.end();
			return;
	},
	no_exists_method:function(res,option){
			res.writeHead(200,{"Content-Type":"text/html"});
			var str="<pre>Error:";
			str+="Could not find '"+option.action_name+"' action in '"+option.controller_name+"_Controller'.";
			str+="</pre>";
			res.write(str);
			res.end();
			return;
	},
	no_exist_view:function(res,option){
			var str="<pre>Error:";
			str+="Not found View File '"+option.viewPath+"'.";
			str+="</pre>";
			res.write(str);
			return;
	}


};