module.exports={

	check:function(url){
		var rt_length=global.rt_web.pages.length;
		for(var n1=0;n1<rt_length;n1++){
			var r_=global.rt_web.pages[n1];

			if(url==r_[0]){

				if(typeof(r_[1])=="string"){
					var split=r_[1].split("@");
					if(split[1]){
						var r_={
							controller:split[0],
							action:split[1],
						};
					}
					else
					{
						var r_={
							view:split[0]
						};
					}
				}

				return r_;
				break;
			}
		}

		return false;

	},

};