module.exports={

	check:function(url){

		if(url=="/"){
			var target_a=["/"];
		}
		else
		{
			var target_a=url.split("/");
			target_a.shift();
		}
	
		var rt_length=rt_web.pages.length;
		for(var n1=0;n1<rt_length;n1++){
			var r_=rt_web.pages[n1];

			if(r_[0]=="/"){
				var target_b=["/"];
			}
			else
			{
				var target_b=r_[0].split("/");
				target_b.shift();
			}

			var juge=true;
			for(var u2=0;u2<target_b.length;u2++){
				for(var u1=0;u1<target_a.length;u1++){

					var test_target="."+target_b[u2]+".";

					var aregmented=false;
					var any=false;
					if(test_target.indexOf("{:")>0 && test_target.indexOf("}")>0){
						aregmented=true;
					}
					if(test_target.indexOf("{:")>0 && test_target.indexOf("?}")>0){
						any=true;
					}

					if(target_a[u1]!=target_b[u2]){
						if(aregmented){
							if(!any){
								if(!target_a[u1]){
									juge=false;
									break;
								}
							}
						}
						else
						{
							juge=false;
							break;
						}
					}
				}
			}

			if(juge){
				var confirm_routing=r_[1];
				break;
			}
		}

		if(confirm_routing){
			if(typeof(confirm_routing)=="string"){

				var split=confirm_routing.split("@");
				if(split[1]){
					var r_={
						controller:split[0],
						action:split[1],
					};
				}
				else
				{
					var r_={
						view:split[0]
					};
				}
			}
			return r_;
		}

		return null;
	},

};