module.exports=class View{
	constructor(res,viewPath,sendData,option){

		this.request=option.request;

		try{
			fs.statSync(viewPath);

			var views=new A10EJS(viewPath,sendData,option);
			res.write(views.out());

		}catch(err){
			errors.out(res,"no_exist_view",{
				viewPath:viewPath
			});
			return false;
		}

		return true;
	}
};